/**
 * Simplified DOMPurify alternative for Smart Bookmarks
 * This provides basic sanitization to prevent XSS without external dependencies
 */
(function(global) {
  const DOMPurify = {
    /**
     * Sanitize a string by escaping HTML entities
     * @param {string} str - String to sanitize
     * @return {string} - Sanitized string
     */
    sanitize: function(str) {
      if (typeof str !== 'string') {
        return '';
      }
      
      // Replace HTML special characters with their entities
      return str
        .replace(/&/g, '&amp;')
        .replace(/</g, '&lt;')
        .replace(/>/g, '&gt;')
        .replace(/"/g, '&quot;')
        .replace(/'/g, '&#039;');
    }
  };
  
  // Make DOMPurify available globally
  global.DOMPurify = DOMPurify;
})(window); 